import React, { useState } from "react";
import logo from "../../assets/logos/shutterstock-logo.jpg";
import sort from "../../assets/icons/arrow_drop_down-24px.svg";
import "./NavBar.scss";
import tippy from 'tippy.js';
import 'tippy.js/dist/tippy.css';

function NavBar() {
  const [isOpen, setIsOpen] = useState(false)
  const boxRef = useRef()
  const tooltipRef= useRef()
const {styles,attributes} = usePopper(boxRef.current, tooltipRef.current)

  const onClickHandler = () => {
      setIsOpen(!isOpen)
  }

    return (
    <div className='nav-bar'>
    <div className='nav-bar__div'>
    <img src={logo} alt='logo' className='nav-bar__logo'></img>
      <div ref={boxRef}  className='nav-bar__menu'>
        <h3 className='nav-bar__title'>images</h3>
        <img onClick={onClickHandler} className='nav-bar__icon' src={sort} alt='drop down'></img>
      </div>
      <div className='nav-bar__menu'>
        <h3 className='nav-bar__title'>footage</h3>
        <img className='nav-bar__icon' src={sort} alt='drop down'></img>
      </div>
      <div className='nav-bar__menu'>
        <h3 className='nav-bar__title'>music</h3>
        <img className='nav-bar__icon' src={sort} alt='drop down'></img>
      </div>
      <div className='nav-bar__menu'>
        <h3 className='nav-bar__title'>templates</h3>
        <img className='nav-bar__icon' src={sort} alt='drop down'></img>
      </div>
      </div>  

      <div ref={tooltipRef}>
      testing
      </div>
    </div>
  );
}

export default NavBar;
